import './secciontodo.css';
import Seccion from './secciontodo';
import favorito1 from '../../assets/img/favorito1.jpg';
import producto2 from '../../assets/img/producto2.jpg';
const seccion=[
   {
   	id:1,
    img:favorito1,
    title:'Roses Cupcake',
    text:'Expresa lo que tienes en mente, con detalles dulces y creativos.',
   },
   {
   	id:2,
    img:producto2,
    title:'Cup Love',
    text:'Los CupCakes son nuestros mejores mensajeros de amor.',
   },
   {
   	id:2,
    img:favorito1,
    title:'Roses Cupcake',
    text:'Expresa lo que tienes en mente, con detalles dulces y creativos.',
   },
]

function Sectindex(){
return(
	<div className="row">
{
seccion.map(section=>{
    <div className="col-12 col-md-4" key={section.id}>
	<Seccion img={section.img} title={section.title} text={text}/>
	</div>
})

}

</div>

	)
};


export default Sectindex;
